mapper.select:
SELECT id
FROM users4
WHERE id=#{id}

SELECT id
FROM users4
WHERE id=#{id} AND pwd=#{pwd}

SELECT num, name, addr01, addr02, addr03, id, pwd, email, career, money, point, b_position, regdate, team, responsibility
FROM users4
WHERE id=#{id}

mapper.update:
UPDATE users4
SET money=money+#{money}
WHERE id=#{id}

mapper.select:
SELECT money
FROM users4
WHERE id=#{id}
